// Prendi l'output dell'AI Agent
let risposta = $input.item.json.output;

// Rimuovi grassetto (**testo** o __testo__)
risposta = risposta.replace(/\*\*([^*]+)\*\*/g, '$1');
risposta = risposta.replace(/__([^_]+)__/g, '$1');

// Rimuovi corsivo (*testo* o _testo_)
risposta = risposta.replace(/\*([^*]+)\*/g, '$1');
risposta = risposta.replace(/_([^_]+)_/g, '$1');

// Rimuovi elenchi puntati (* item o - item o ‚Ä¢ item)
risposta = risposta.replace(/^\s*[\*\-‚Ä¢]\s+/gm, '');

// Rimuovi liste numerate (1. item, 2. item, etc.)
risposta = risposta.replace(/^\s*\d+\.\s+/gm, '');

// Rimuovi backtick (`codice`)
risposta = risposta.replace(/`([^`]+)`/g, '$1');

// Rimuovi intestazioni (# Titolo, ## Sottotitolo)
risposta = risposta.replace(/^#+\s+/gm, '');

// Rimuovi righe vuote multiple (lascia max 2 newline consecutive)
risposta = risposta.replace(/\n{3,}/g, '\n\n');

// Rimuovi spazi extra all'inizio delle righe
risposta = risposta.replace(/^\s+/gm, '');

// Pulisci la risposta
const rispostaPulita = risposta.trim();

// Determina se √® stato trovato o meno
const nonTrovato = rispostaPulita.includes("non ho trovato questa informazione");
const ticketMenzionato = rispostaPulita.toLowerCase().includes("ticket") || rispostaPulita.toLowerCase().includes("supporto");

// Ritorna il JSON con tutti i dati mappati alle colonne di Airtable
return {
  json: {
    Risposta: rispostaPulita,
    Domanda: $input.item.json.chatInput || '',
    Timestamp: new Date().toISOString(),
    Trovato: !nonTrovato && !ticketMenzionato,
    Ticket: nonTrovato || ticketMenzionato
  }
};

// Prendi tutti i record da Airtable (da Search records node)
const records = $input.all().map(item => item.json);

// Funzione escape HTML
function escapeHtml(text) {
  if (!text) return '';
  return String(text)
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#039;");
}

// Calcola statistiche
const totalRecords = records.length;
const ticketCount = records.filter(r => {
  const resp = (r.Risposta || '').toLowerCase();
  return resp.includes('ticket') || resp.includes('mi dispiace');
}).length;
const resolvedCount = totalRecords - ticketCount;
const resolvedPercentage = totalRecords > 0 ? Math.round((resolvedCount / totalRecords) * 100) : 0;

// Calcola temi pi√π richiesti (con stopWords estese e filtro verbi)
const stopWords = new Set([
  'di', 'a', 'in', 'con', 'su', 'per', 'il', 'la', 'i', 'le', 'un', 'una', 'che', 'non', '√®', 'e', 'ho', 'come', 'posso', 'cosa', 'risiko',
  'sono', 'hai', 'pu√≤', 'devo', 'deve', 'puoi', 'vuoi', 'voglio', 'fare', 'avere', 'essere', 'quando', 'dove', 'perch√®', 'perch√®', 'quale', 'quali',
  'questo', 'questa', 'questi', 'queste', 'quello', 'quella', 'quelli', 'quelle', 'mio', 'mia', 'miei', 'mie', 'tuo', 'tua', 'tuoi', 'tue',
  'suo', 'sua', 'suoi', 'sue', 'nostro', 'nostra', 'nostri', 'nostre', 'vostro', 'vostra', 'vostri', 'vostre', 'loro',
  'pi√π', 'molto', 'poco', 'tanto', 'troppo', 'ogni', 'tutto', 'tutti', 'tutte', 'alcune', 'alcuni', 'nessuno', 'nessuna',
  'anche', 'ancora', 'gi√†', 'mai', 'sempre', 'spesso', 'volte', 'durante', 'dopo', 'prima', 'poi', 'quindi', 'per√≤', 'oppure',
  'ciao', 'grazie', 'prego', 'scusa', 'aiuto', 'aiutare', 'dire', 'sapere', 'capire', 'spiegare', 'chiedere',
  'termina', 'terminare', 'presenti', 'presente', 'qual', 'comincia', 'inizia', 'finisce', 'invece', 'viene', 'vanno', 'fanno',
  'servono', 'serve', 'usano', 'usare', 'hanno', 'aveva', 'avevo', 'potrei', 'vorrei', 'dovrei', 'sarebbe', 'fosse', 'siano',
  'stata', 'stato', 'stati', 'state', 'stare', 'stai', 'stanno',
  'quante', 'quanti', 'quanto', 'quanta', 'allinizio', 'necessari', 'necessarie', 'necessario', 'necessaria'
]);

const wordCount = {};
records.forEach(item => {
  if (item.Domanda) {
    item.Domanda.toLowerCase().split(/\s+/).forEach(word => {
      const cleanWord = word.replace(/[^\w√†√®√©√¨√≤√π]/gi, '');
      if (cleanWord.length >= 5 && 
          !stopWords.has(cleanWord) && 
          !cleanWord.endsWith('are') && 
          !cleanWord.endsWith('ere') && 
          !cleanWord.endsWith('ire') &&
          !cleanWord.endsWith('ono') &&
          !cleanWord.endsWith('ano')) {
        wordCount[cleanWord] = (wordCount[cleanWord] || 0) + 1;
      }
    });
  }
});

const topThemes = Object.entries(wordCount)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 12)
  .map(([word, count]) => `
    <div class="col-md-3 col-sm-4 col-6 mb-3">
      <div class="theme-card p-3 text-center">
        <h5 class="mb-1">${escapeHtml(word)}</h5>
        <span class="badge bg-danger">${count} richieste</span>
      </div>
    </div>
  `).join('');

// Tabella risposte (ultimi 30 record per performance)
const tableRows = records.slice(0, 30).map(item => {
  const date = new Date(item['Data e ora'] || item.createdTime);
  const dateStr = !isNaN(date.getTime()) ? date.toLocaleString('it-IT', { 
    day: '2-digit', 
    month: '2-digit', 
    year: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
  }) : 'N/A';

  const domanda = escapeHtml(item.Domanda || 'N/A');
  const risposta = escapeHtml(item.Risposta || 'N/A');
  const isTicket = risposta.toLowerCase().includes('ticket') || risposta.toLowerCase().includes('mi dispiace');
  const statusClass = isTicket ? 'bg-warning text-dark' : 'bg-success';
  const statusText = isTicket ? '‚ö†Ô∏è Ticket' : '‚úì Risolta';

  return `
    <tr>
      <td class="text-nowrap">${dateStr}</td>
      <td><strong>${domanda}</strong></td>
      <td>${risposta.substring(0, 120)}${risposta.length > 120 ? '...' : ''}</td>
      <td><span class="badge ${statusClass}">${statusText}</span></td>
    </tr>
  `;
}).join('');

// Grafico dati per giorno della settimana
const days = ['Domenica', 'Luned√¨', 'Marted√¨', 'Mercoled√¨', 'Gioved√¨', 'Venerd√¨', 'Sabato'];
const dayCounts = new Array(7).fill(0);

records.forEach(item => {
  const dateStr = item['Data e ora'];
  if (dateStr) {
    const d = new Date(dateStr);
    if (!isNaN(d.getTime())) {
      const dayOfWeek = d.getDay();
      dayCounts[dayOfWeek]++;
    }
  }
});

const chartData = JSON.stringify({
  labels: days,
  datasets: [{
    label: 'Risposte per giorno',
    data: dayCounts,
    backgroundColor: 'rgba(220, 53, 69, 0.8)',
    borderColor: 'rgba(220, 53, 69, 1)',
    borderWidth: 2,
    borderRadius: 8
  }]
});

// HTML completo con stile migliorato
const html = `<!DOCTYPE html>
<html lang="it">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
